
from unittest import TestCase 
from exofile import Text

class TestText (TestCase):

  def test_text (self):
    TEST_TEXT = ""
    text = Text.deserialize(TEST_TEXT)
    self.maxDiff = 65536
    self.assertEqual(text, "test text")
    self.assertEqual(text.serialize(), TEST_TEXT)

  def test_text2 (self):
    TEST_TEXT = ""
    text = Text.deserialize(TEST_TEXT)
    self.maxDiff = 65536
    self.assertEqual(text, "テストテキスト")
    self.assertEqual(text.serialize(), TEST_TEXT)

  def test_text3 (self):
    TEST_TEXT = ""
    text = Text("テストテキスト")
    self.maxDiff = 65536
    self.assertEqual(text, "テストテキスト")
    self.assertEqual(text.serialize(), TEST_TEXT)

  def test_text_multi_line (self):
    TEST_TEXT = ""
    text = Text("芋\n米\n葡萄\n大麦\nみないつかは酒になる")
    self.maxDiff = 65536
    self.assertEqual(text, "芋\n米\n葡萄\n大麦\nみないつかは酒になる")
    self.assertEqual(text.serialize(), TEST_TEXT)

  def test_text_error (self):
    Text.deserialize("0" * Text.MAX_SERIALIZED_LENGTH)
    with self.assertRaises(ValueError):
      Text.deserialize("0" * (Text.MAX_SERIALIZED_LENGTH +1))

  def test_text_error2 (self):
    text = Text("0" * Text.MAX_DESERIALIZED_LENGTH)
    text.serialize()
    with self.assertRaises(ValueError):
      text = Text("0" * (Text.MAX_DESERIALIZED_LENGTH + 1))
      text.serialize()
